import { Component } from '@angular/core';
import { Observable } from 'rxjs';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-register-gost',
  templateUrl: './register-gost.component.html',
  styleUrls: ['./register-gost.component.css']
})
export class RegisterGostComponent {
  username: string = "";
  password: string = "";
  securityQuestion: string = "";
  securityAnswer: string = "";
  name: string = "";
  surname: string = "";
  gender: string = ""; 
  address: string = "";
  phoneNumber: string = "";
  email: string = "";
  creditCardNumber: string = "";
  errorMessage: string = "";
  fileForUpload: File | null = null;
  successMessage: string = "";

  constructor(private userService: UserService, private router: Router){}

  onFileSelected(event: any): void {
    const file: File = event.target.files[0];
    const reader = new FileReader();

    reader.onload=(e)=>{
      const img = new Image();
      img.onload=()=>{
        const width = img.width;
        const height = img.height;
        if(width < 100 || height < 100 || width > 300 || height > 300){
          this.errorMessage = "Profilna slika nije odgovarajucih dimenzija";
          this.fileForUpload = null;
        } else {
          this.errorMessage = ""
          this.fileForUpload = file;
        }
      }
      img.src = e.target.result.toString();
    }
    reader.readAsDataURL(file);
  }
  register(){
    const passwordRegex = /^(?=[a-zA-Z])(?=.*[A-Z])(?=.*[a-z].*[a-z].*[a-z])(?=.*\d)(?=.*[!@#$%^&*()_+{}\[\]:;<>,.?~\\/-]).{6,10}$/;
    if(this.username == "" || this.password == "" || this.securityQuestion == "" || this.securityAnswer == "" || this.name == "" || this.surname == "" || this.gender == "" || this.address == "" || this.phoneNumber == "" || this.email == "" || this.creditCardNumber == "") {
      this.errorMessage = "Niste uneli sve potrebne podatke!"
    } else {
      if(passwordRegex.test(this.password) != true){
        this.errorMessage = "Lozinka nije u odgovarajucem formatu!"
      } else {
        this.errorMessage = ""
        this.userService.registerGost(this.username, this.password, this.securityQuestion, this.securityAnswer, this.name, this.surname, this.gender, this.address, this.phoneNumber, this.email, this.fileForUpload, this.creditCardNumber).subscribe(response=>{
          if(response == "Uspesno ste poslali zahtev za registracijom!"){
            this.successMessage = response;
          } else {
            this.errorMessage = response;
          }
        })
      }
    }
  }
}
<div class="login-container">
  <h2>Register</h2>
  <div class="input-field">
    <label for="username">Username</label>
    <input type="text" id="username" [(ngModel)]="username">
  </div>
  <div class="input-field">
    <label for="password">Password</label>
    <input type="password" id="password" [(ngModel)]="password">
  </div>
  <div class="input-field">
    <label for="securityQuestion">Sigurnosno pitanje</label>
    <input type="text" id="pitanje" [(ngModel)]="securityQuestion">
  </div>
  <div class="input-field">
    <label for="securityQuestionAnswer">Odgovor</label>
    <input type="text" id="odgovor" [(ngModel)]="securityAnswer">
  </div>
  <div class="input-field">
    <label for="ime">Ime</label>
    <input type="text" id="ime" [(ngModel)]="name">
  </div>
  <div class="input-field">
    <label for="prezime">Prezime</label>
    <input type="text" id="prezime" [(ngModel)]="surname">
  </div>
  <div class="input-field radio-group">
    <div class="radio-label">
      <input type="radio" id="musko" name="gender" [(ngModel)]="gender" value="musko">
      <label for="musko">Musko</label>
    </div>
    <div class="radio-label">
      <input type="radio" id="zensko" name="gender" [(ngModel)]="gender" value="zensko">
      <label for="zensko">Zensko</label>
    </div>
  </div>
  <div class="input-field">
    <label for="adresa">Adresa</label>
    <input type="text" id="adresa" [(ngModel)]="address">
  </div>
  <div class="input-field">
    <label for="telefon">Telefon</label>
    <input type="text" id="telefon" [(ngModel)]="phoneNumber">
  </div>
  <div class="input-field">
    <label for="email">Email</label>
    <input type="text" id="email" [(ngModel)]="email">
  </div>
  <div class="input-field">
    Profilna slika
    <input type="file" id="profilePicture" (change)="onFileSelected($event)" name="file" accept="image/*" required>
  </div>
  <div class="input-field">
    <label for="cardNumber">Broj kreditne kartice</label>
    <input type="text" id="cardNumber" [(ngModel)]="creditCardNumber">
  </div>
  <button (click)="register()">Register</button>
  <div class="error-message">{{errorMessage}}</div>
  <div class="success-message">{{successMessage}}</div>
</div>
