import { Component, OnInit } from '@angular/core';
import { restoran } from '../models/restoran';
import { recenzija } from '../models/recenzija';
import { RestoranService } from '../services/restoran.service';
import { Router } from '@angular/router';
import { gost } from '../models/gost';

@Component({
  selector: 'app-restoran',
  templateUrl: './restoran.component.html',
  styleUrls: ['./restoran.component.css']
})
export class RestoranComponent implements OnInit{
  restoran: restoran = null;
  recenzije: recenzija[] = [];
  reservation = {
    date: '',
    people: 1,
    requests: ''
  };
  validationMessage: string = '';
  validationMessageSuccess: string = '';
  isReservationValid: boolean = true;
  gost: gost = null;

  constructor(private restoranService: RestoranService, private router: Router){}

  ngOnInit(): void {
    let rest = localStorage.getItem('restoran');
    let usr = localStorage.getItem('ulogovan');
    if(usr != null) this.gost = JSON.parse(usr);
    this.restoranService.getRestoranWithNaziv(rest).subscribe(rez=>{
      this.restoran = rez;
      this.restoranService.getRecenzijeForRestoran(rest).subscribe(rec=>{
        this.recenzije = rec;
      })
    })
  }

  makeReservation(){
    const date = new Date(this.reservation.date);
    const now = new Date();

    if(date < now){
      this.validationMessage = 'Datum i vreme ne mogu biti u proslosti!'
      this.isReservationValid = false;
      return;
    }
    if(this.reservation.people < 1){
      this.validationMessage = 'Broj osoba ne moze biti manji od 1!';
      this.isReservationValid = false;
      return;
    }

    this.restoranService.kreirajRezervaciju(date, this.reservation.people, this.reservation.requests, this.restoran.naziv, this.gost.username).subscribe(rez=>{
      if(rez == "Uspesno ste poslali zahtev za rezervacijom!"){
        this.validationMessage = '';
        this.validationMessageSuccess = rez;
      } else {
        this.validationMessageSuccess = '';
        this.validationMessage = rez;
      }
    })
  }
}
<div class="container">
    <header>
        <h1>Detalji Restorana</h1>
    </header>
    <section class="restaurant-details" *ngIf="restoran">
        <h2>{{ restoran.naziv }}</h2>
        <div class="details">
            <p><strong>Adresa:</strong> {{ restoran.adresa }}</p>
            <p><strong>Tip restorana:</strong> {{ restoran.tip }}</p>
            <p><strong>Telefon:</strong> {{ restoran.telefon }}</p>
        </div>
        <div class="comments">
            <h3>Komentari</h3>
            <ul>
                <li *ngFor="let recenzija of recenzije">
                    <p><strong>Ocena:</strong> {{ recenzija.ocena }} / 5</p>
                    <p><strong>Komentar:</strong> {{ recenzija.komentar }}</p>
                </li>
            </ul>
        </div>
        <div class="reservation">
            <h3>Napravite Rezervaciju</h3>
            <form (ngSubmit)="makeReservation()">
                <div class="form-group">
                    <label for="date">Datum i vreme:</label>
                    <input type="datetime-local" id="date" name="date" [(ngModel)]="reservation.date" required>
                </div>
                <div class="form-group">
                    <label for="people">Broj osoba:</label>
                    <input type="number" id="people" name="people" [(ngModel)]="reservation.people" min="1" required>
                </div>
                <div class="form-group">
                    <label for="requests">Dodatni zahtevi:</label>
                    <textarea id="requests" name="requests" [(ngModel)]="reservation.requests"></textarea>
                </div>
                <button type="submit">Rezerviši</button>
                <div class="validation-messages">
                    <span style="color: red;" *ngIf="validationMessage != ''">{{ validationMessage }}</span>
                    <span style="color: green;" *ngIf="validationMessageSuccess != ''">{{ validationMessageSuccess }}</span>
                </div>
            </form>
        </div>
    </section>
    <section *ngIf="!restoran">
        <p>Učitavanje...</p>
    </section>
</div>
