import { Component, Input, OnInit, ViewChild, ElementRef } from '@angular/core';
import { LayoutResponse } from '../models/LayoutResponse';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-layout',
  templateUrl: './layout.component.html',
  styleUrls: ['./layout.component.css']
})
export class LayoutComponent implements OnInit {

  @Input() layoutData: any; 
  @ViewChild('canvas', { static: true }) canvas: ElementRef<HTMLCanvasElement>;
  private ctx: CanvasRenderingContext2D;

  constructor(private http: HttpClient) {}

  private fetchLayout(): void {
    this.http.get<LayoutResponse>('http://localhost:4000/restoran/layout')
      .subscribe({
        next: (data: LayoutResponse) => {
          this.layoutData = data; 
        },
        error: (error: any) => {
          console.error('Error fetching layout:', error);
        }
      });
  }
  ngOnInit(): void {
    this.fetchLayout();
    console.log(this.layoutData)
    this.ctx = this.canvas.nativeElement.getContext('2d');

    if (this.layoutData) {
      this.drawLayout(this.layoutData); 
    }
  }

  private drawLayout(data: any): void {
    this.ctx.clearRect(0, 0, this.canvas.nativeElement.width, this.canvas.nativeElement.height);

    data.tables.forEach(table => {
      this.ctx.fillStyle = 'green';
      this.ctx.fillRect(table.x, table.y, 50, 50);
      this.ctx.fillStyle = 'black';
      this.ctx.fillText(`Table ${table.id}`, table.x, table.y);
    });

    this.ctx.fillStyle = 'blue';
    this.ctx.fillRect(data.kitchen.x, data.kitchen.y, 100, 100);
    this.ctx.fillStyle = 'white';
    this.ctx.fillText('Kitchen', data.kitchen.x, data.kitchen.y);

    this.ctx.fillStyle = 'purple';
    this.ctx.fillRect(data.toilets.x, data.toilets.y, 50, 50);
    this.ctx.fillStyle = 'white';
    this.ctx.fillText('Toilets', data.toilets.x, data.toilets.y);
  }
}
