import { Component } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login-admin',
  templateUrl: './login-admin.component.html',
  styleUrls: ['./login-admin.component.css']
})
export class LoginAdminComponent {
  username: string = ""
  password: string = ""
  message: string = ""
  constructor(private userService: UserService, private router: Router){}

  login(){
    if(this.username == "" || this.password == ""){
      this.message = "Niste uneli sve informacije!"
    } else {
      this.userService.login_admin(this.username, this.password).subscribe((rez: any)=>{
        if(rez.message == "Uspesno"){
          this.message = "";
          localStorage.setItem('ulogovan', JSON.stringify(rez.user));
          this.router.navigate(['adminDashboard']);
        } else {
          this.message = rez.message;
        }
      })
    }
  }
}
<div class="login-container">
    <h2 align="center">Login</h2>
    <div class="input-field">
      <label for="username">Username:</label>
      <input type="text" id="username" [(ngModel)]="username">
    </div>
    <div class="input-field">
      <label for="password">Password:</label>
      <input type="password" id="password" [(ngModel)]="password">
    </div>
    <div class="button-container">
      <button (click)="login()">Login</button>
    </div>
    <div class="error-message">{{message}}</div>
  </div>
