: 1px solid #ccc;\n    border-radius: 4px;\n    box-sizing: border-box;\n}\n\n.restaurant-list[_ngcontent-%COMP%]   button[_ngcontent-%COMP%]{\n    margin-top: 10px;\n    margin-bottom: 10px;\n    margin-left: 10px;\n    padding: 10px;\n    background: #8561a8;\n    color: white;\n    border: none;\n    cursor: pointer;\n    border-radius: 4px; \n    text-align: center;\n}\n\n.restaurant-list[_ngcontent-%COMP%]   button[_ngcontent-%COMP%]:hover{\n    background: #64427a; \n}\n.search[_ngcontent-%COMP%]   button[_ngcontent-%COMP%] {\n    margin-top: 10px;\n    padding: 10px;\n    background: #8561a8;\n    color: white;\n    border: none;\n    cursor: pointer;\n    border-radius: 4px; \n    text-align: center;\n}\n\n.search[_ngcontent-%COMP%]   button[_ngcontent-%COMP%]:hover {\n    background: #64427a; \n}\n\n.plain-text-link[_ngcontent-%COMP%] {\n    color: inherit; \n    text-decoration: none; \n    cursor: pointer; \n}\n\n.plain-text-link[_ngcontent-%COMP%]:hover {\n    text-decoration: underline; \n}\n\n.review-form[_ngcontent-%COMP%] {\n    margin-top: 20px; \n    padding: 20px;\n    border: 1px solid #ccc;\n    border-radius: 5px;\n    width: 80%; \n    margin: 20px auto;\n}\n  \n.stars[_ngcontent-%COMP%]   .star[_ngcontent-%COMP%] {\n    font-size: 24px;\n    cursor: pointer;\n    color: #ccc;\n}\n  \n.stars[_ngcontent-%COMP%]   .star.filled[_ngcontent-%COMP%] {\n    color: gold;\n}\n  \n.buttonDisplay[_ngcontent-%COMP%]{\n    display: flex;\n    justify-content: center; \n}\n\n.chart-container[_ngcontent-%COMP%] {\n    width: 100%;\n    margin: 20px 0;\n}\n\ncanvas[_ngcontent-%COMP%] {\n    width: 100%;\n    height: auto;\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
