import { Component, OnInit, ViewChild, ElementRef, AfterViewInit } from '@angular/core';
import { konobar } from '../models/konobar';
import { SafeUrl, DomSanitizer } from '@angular/platform-browser';
import { last, lastValueFrom } from 'rxjs';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import { rezervacija } from '../models/rezervacija';
import { RezervacijeService } from '../services/rezervacije.service';
import { narudzbina } from '../models/narudzbina';
import { NarudzbinaService } from '../services/narudzbina.service';
import { Chart, ChartConfiguration } from 'chart.js';


@Component({
  selector: 'app-konobar',
  templateUrl: './konobar.component.html',
  styleUrls: ['./konobar.component.css']
})
export class KonobarComponent implements OnInit, AfterViewInit{
  @ViewChild('columnChart') columnChart: ElementRef;
  @ViewChild('pieChart') pieChart: ElementRef;
  @ViewChild('histogramChart') histogramChart: ElementRef;
  chartPita: any;
  chartKolona: any;
  chartHistogram: any;
  korisnik: konobar;
  fileDownloaded: SafeUrl | null = null;
  fileForUpload: File | null = null;
  ime: string;
  prezime: string;
  adresa: string;
  email: string;
  telefon: string;
  successMessage: string = "";
  errorMessage: string = "";
  flagProfil: boolean = true;
  flagRezervacije: boolean = false;
  flagNarudzbine: boolean = false;
  flagStatistika: boolean = false;
  narudzbine: narudzbina[] = [];
  neobradjeneRezervacije: rezervacija[] = [];
  komentar: string = "";
  errorKomentar: string = "";
  odbijFlag: boolean = false;
  rezervacijeZaPotvrdu: rezervacija[] = [];
  checkBoxFlag: boolean = false;
  minvreme: number = -1;
  errorVreme: string = "";
  daysOfWeek: string[] = ['PON', 'PON', 'UTO', 'SRE', 'CET', 'PET', 'SUB', 'NED'];

  constructor(private userService: UserService, private sanitizer: DomSanitizer, private router: Router, private rezervacijaService: RezervacijeService, private narudzbineService: NarudzbinaService){}

  async ngOnInit(){
    this.successMessage = "";
    this.errorMessage = "";
    this.errorKomentar = "";
    this.odbijFlag = false;
    this.checkBoxFlag = false;
    this.minvreme = -1;
    this.errorVreme = "";
    let usr = localStorage.getItem('ulogovan');
    if(usr != null) this.korisnik = JSON.parse(usr);
    this.ime = this.korisnik.name;
    this.prezime = this.korisnik.surname;
    this.adresa = this.korisnik.address;
    this.email = this.korisnik.email;
    this.telefon = this.korisnik.phoneNumber;
    try{
      let data = await lastValueFrom(this.userService.getFileKonobar(this.korisnik.username));
      const blob = new Blob([data]);
      const url = URL.createObjectURL(blob);
      this.fileDownloaded = this.sanitizer.bypassSecurityTrustUrl(url);
      let neobradjeneRezervacije = await lastValueFrom(this.rezervacijaService.getNeobradjeneRezervacije(this.korisnik.restoran));
      this.neobradjeneRezervacije = neobradjeneRezervacije;
      this.neobradjeneRezervacije.sort((a, b) => {
        let dateA = new Date(a.datum);
        let dateB = new Date(b.datum);

        return dateA.getTime() - dateB.getTime();
      });
      this.rezervacijeZaPotvrdu = await lastValueFrom(this.rezervacijaService.getRezervacijeZaPotvrdu(this.korisnik.username));
      let narudzbineTren = await lastValueFrom(this.narudzbineService.getTrenutneNarudzbine(this.korisnik.restoran));
      for(let narudzbina of narudzbineTren){
        for(let deo of narudzbina.deoNarudzbine){
          let jelo = await lastValueFrom(this.narudzbineService.getJelo(deo.jelo));
          deo.jeloObjekat = jelo;
        }
      }
      this.narudzbine = narudzbineTren;

    } catch (error){
      console.log(error)
    }
  }

  async ngAfterViewInit() {
    try{
      let povratna: any = await lastValueFrom(this.userService.getInfoForStatistics(this.korisnik.username, this.korisnik.restoran));
      if (this.columnChart && this.columnChart.nativeElement) {
        this.populateKolona(this.columnChart.nativeElement, povratna.dijagramKolona);
      }
      if (this.pieChart && this.pieChart.nativeElement) {
        this.populatePita(this.pieChart.nativeElement, povratna.dijagramPita);
      }
      if (this.histogramChart && this.histogramChart.nativeElement) {
        this.populateHistogram(this.histogramChart.nativeElement, povratna.dijagramHistogram);
      }
    } catch(error){
      console.log(error);
    }
  }

  onFileSelected(event: any): void {
    const file: File = event.target.files[0];
    const reader = new FileReader();

    reader.onload=(e)=>{
      const img = new Image();
      img.onload=()=>{
        const width = img.width;
        const height = img.height;
        if(width < 100 || height < 100 || width > 300 || height > 300){
          this.errorMessage = "Profilna slika nije odgovarajucih dimenzija";
          this.fileForUpload = null;
        } else {
          this.errorMessage = ""
          this.fileForUpload = file;
        }
      }
      img.src = e.target.result.toString();
    }
    reader.readAsDataURL(file);
  }

  async updateProfile(){
    this.successMessage = "";
    this.errorMessage = "";
    this.korisnik.name = this.ime;
    this.korisnik.surname = this.prezime;
    this.korisnik.address = this.adresa;
    this.korisnik.email = this.email;
    this.korisnik.phoneNumber = this.telefon;
    try{
      let rez = await lastValueFrom(this.userService.updateProfileKonobar(this.korisnik));
      localStorage.removeItem('ulogovan')
      localStorage.setItem('ulogovan', JSON.stringify(rez));
      this.successMessage = "Uspesno ste azurirali profil!"
      if(this.fileForUpload != null){
        let res = await lastValueFrom(this.userService.updatePictureKonobar(this.korisnik.username, this.fileForUpload))
        localStorage.removeItem('ulogovan')
        localStorage.setItem('ulogovan', JSON.stringify(res));
        this.successMessage = "Uspesno ste azurirali profil!";
        let data = await lastValueFrom(this.userService.getFileKonobar(this.korisnik.username))
        const blob = new Blob([data]);
        const url = URL.createObjectURL(blob);
        this.fileDownloaded = this.sanitizer.bypassSecurityTrustUrl(url);
      }
    } catch (error){
      console.log(error)
    }
  }

  profil() {
    this.flagProfil = true;
    this.flagRezervacije = false;
    this.flagNarudzbine = false;
    this.flagStatistika = false;
  }

  rezervacije() {
    this.flagProfil = false;
    this.flagRezervacije = true;
    this.flagNarudzbine = false;
    this.flagStatistika = false;
  }

  dostave() {
    this.flagProfil = false;
    this.flagRezervacije = false;
    this.flagNarudzbine = true;
    this.flagStatistika = false;
  }

  statistika(){
    this.flagProfil = false;
    this.flagRezervacije = false;
    this.flagNarudzbine = false;
    this.flagStatistika = true;
  }


  formatDate(datum: Date): string {
    let date = new Date(datum);
    date.setHours(date.getHours() - 2);
    const month = this.padWithZero(date.getMonth() + 1); 
    const day = this.padWithZero(date.getDate());
    const year = date.getFullYear();
    let hours = this.padWithZero(date.getHours());
    const minutes = this.padWithZero(date.getMinutes());
    const seconds = this.padWithZero(date.getSeconds());

    const formattedDate = `${day}.${month}.${year} ${hours}:${minutes}:${seconds}`;

    return formattedDate;
  }

  padWithZero(num: number): string {
    return num < 10 ? '0' + num : num.toString();
  }

  async potvrdi(id: number){
    try{
      let rezervacija = await lastValueFrom(this.rezervacijaService.getRezervacija(id))
      localStorage.setItem('rezervacija', JSON.stringify(rezervacija));
      this.router.navigate(['layout'])
    } catch(error){
      console.log(error)
    }
  }

  odbijFl(){
    this.odbijFlag = true;
  }

  async odbij(id: number){
    try{
      if(this.komentar == ""){
        this.errorKomentar = "Unos komentara je obavezan!";
      } else {
        let string = await lastValueFrom(this.rezervacijaService.odbijRezervaciju(this.komentar, id, this.korisnik.username));
        this.ngOnInit();
      }
    } catch(error){
      console.log(error)
    }
  }

  async potvrdiDolazak(id: number){
    try{
      let str = await lastValueFrom(this.rezervacijaService.potvrdiDolazak(id));
      this.ngOnInit();
    } catch(error){
      console.log(error)
    }
  }

  async odbijDolazak(id: number){
    try{
      let str = await lastValueFrom(this.rezervacijaService.odbijDolazak(id));
      this.ngOnInit();
    } catch(error){
      console.log(error)
    }
  }

  async odbijNarudzbinu(id: number){
    try{
      let str = await lastValueFrom(this.narudzbineService.odbijNarudzbinu(id));
      this.ngOnInit();
    } catch(error){
      console.log(error)
    }
  }

  prikaziCheckBox(){
    this.checkBoxFlag = true;
  }

  async potvrdiNarudzbinu(id: number){
    try{
      if(this.minvreme == -1){
        this.errorVreme = "Morate odabrati opciju za procenjeno vreme dostave!"
      } else {
        this.errorVreme = "";
        if(this.minvreme == 20){
          let str = await lastValueFrom(this.narudzbineService.potvrdiNarudzbinu(id, 20, 30));
        } else if (this.minvreme == 30){
          let str = await lastValueFrom(this.narudzbineService.potvrdiNarudzbinu(id, 30, 40));
        } else {
          let str = await lastValueFrom(this.narudzbineService.potvrdiNarudzbinu(id, 50, 60));
        }
        this.ngOnInit()
      }
    } catch(error){
      console.log(error)
    }
  }

  populateKolona(chartElement: ElementRef, dijagramKolona: any[]){
    console.log(dijagramKolona)
    const ctx = chartElement.nativeElement.getContext('2d');
    const config: ChartConfiguration = {
      type: 'bar',
      data: {
        labels: Object.keys(dijagramKolona),
        datasets: [{
          label: 'Broj gostiju',
          data: Object.values(dijagramKolona),
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1
        }]
      },
      options:{
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    }
    this.chartKolona = new Chart(ctx, config)
  }

  populatePita(chartElement: ElementRef, dijagramPita: any[]){
    console.log(dijagramPita);
    const ctx = chartElement.nativeElement.getContext('2d');
    const config: ChartConfiguration = {
      type: 'pie',
      data: {
        labels: dijagramPita.map(item => item.username),
        datasets: [{
          label: 'Broj gostiju',
          data: dijagramPita.map(item => item.sumBrojGostiju),
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)',
            'rgba(75, 192, 192, 0.2)',
            'rgba(153, 102, 255, 0.2)',
            'rgba(255, 159, 64, 0.2)'
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)',
            'rgba(75, 192, 192, 1)',
            'rgba(153, 102, 255, 1)',
            'rgba(255, 159, 64, 1)'
          ],
          borderWidth: 1
        }]
      }
    }
    this.chartPita = new Chart(ctx, config)
  }

  populateHistogram(chartElement: ElementRef, dijagramHistogram: any[]){
    console.log(dijagramHistogram)
    const ctx = chartElement.nativeElement.getContext('2d');
    const config: ChartConfiguration = {
      type: 'bar',
      data: {
        labels: Object.keys(dijagramHistogram),
        datasets: [{
          label: 'Prosecan broj rezervacija',
          data: Object.values(dijagramHistogram),
          backgroundColor: 'rgba(153, 102, 255, 0.2)',
          borderColor: 'rgba(153, 102, 255, 1)',
          borderWidth: 1
        }]
      },
      options: {
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    }
    this.chartHistogram = new Chart(ctx, config)
  }
}
<div class="container">
    <header>
        <h1>Dobrodošli u sistem restorana</h1>
        <div class="buttons">
            <button (click)="profil()">Profil</button>
            <button (click)="rezervacije()">Rezervacije</button>
            <button (click)="dostave()">Dostave</button>
            <button (click)="statistika()">Statistika</button>
        </div>
    </header>
    <section class="profile" *ngIf="flagProfil == true">
        <h2>Profil</h2>
        <div class="profile-info">
            <div class="profile-picture">
                <img [src]="fileDownloaded" alt="Downloaded Image">
                <input type="file" accept="image/*" (change)="onFileSelected($event)">
                <span style="color: red">{{errorMessage}}</span>
            </div>
            <div class="profile-details">
                <div class="input-field">
                    <label for="ime">Ime:</label>
                    <input type="text" id="ime" name="ime" [(ngModel)]="ime">
                </div>
                <div class="input-field">
                    <label for="prezime">Prezime:</label>
                    <input type="text" id="prezime" name="prezime" [(ngModel)]="prezime">
                </div>
                <div class="input-field">
                    <label for="adresa">Adresa:</label>
                    <input type="text" id="adresa" name="adresa" [(ngModel)]="adresa">
                </div>
                <div class="input-field">
                    <label for="email">E-mail adresa:</label>
                    <input type="email" id="email" name="email" [(ngModel)]="email">
                </div>
                <div class="input-field">
                    <label for="telefon">Kontakt telefon:</label>
                    <input type="tel" id="telefon" name="telefon" [(ngModel)]="telefon">
                </div>
                <div class="buttons">
                    <button (click)="updateProfile()">Ažuriraj profil</button>
                    <span style="color: green;">{{successMessage}}</span>
                </div>
            </div>
        </div>
    </section>
    <section *ngIf="flagRezervacije == true" class="restaurant-list">
        <h2>Lista neobradjenih rezervacija</h2>
        <table>
            <thead>
                <tr>
                    <th>Gost</th>
                    <th>Datum</th>
                    <th>Sto</th>
                    <th>Broj gostiju</th>
                    <th>Akcija</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let rezervacija of neobradjeneRezervacije">
                    <td>{{ rezervacija.gost }}</td>
                    <td>{{ formatDate(rezervacija.datum) }}</td>
                    <td>{{ rezervacija.sto }}</td>
                    <td>{{ rezervacija.brojGostiju }}</td>
                    <td class="buttonDisplay">
                        <button (click)="potvrdi(rezervacija.id)" *ngIf="odbijFlag == false">Potvrdi</button>
                        <button (click)="odbijFl()" *ngIf="odbijFlag == false">Odbij</button>
                        <section *ngIf="odbijFlag == true">
                            Komentar: <input type="text" [(ngModel)]="komentar">
                            <span style="color: red;">{{errorKomentar}}</span>
                            <button (click)="odbij(rezervacija.id)" *ngIf="odbijFlag == true">Odbij</button>
                        </section>
                    </td>
                </tr>
            </tbody>
        </table>
        <h2>Lista rezervacija za potvrdu dolaska</h2>
        <table>
            <thead>
                <tr>
                    <th>Gost</th>
                    <th>Datum</th>
                    <th>Sto</th>
                    <th>Broj gostiju</th>
                    <th>Akcija</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let rezervacija of rezervacijeZaPotvrdu">
                    <td>{{ rezervacija.gost }}</td>
                    <td>{{ formatDate(rezervacija.datum) }}</td>
                    <td>{{ rezervacija.sto }}</td>
                    <td>{{ rezervacija.brojGostiju }}</td>
                    <td class="buttonDisplay">
                        <button (click)="potvrdiDolazak(rezervacija.id)">Potvrdi dolazak</button>
                        <button (click)="odbijDolazak(rezervacija.id)">Potvrdi nedolazak</button>
                    </td>
                </tr>
            </tbody>
        </table>
    </section>
    <section *ngIf="flagNarudzbine == true" class="restaurant-list">
        <h2>Lista neobradjenih narudzbina</h2>
        <table>
            <thead>
                <tr>
                    <th>Gost</th>
                    <th>Datum</th>
                    <th>Sastav narudzbine</th>
                    <th>Akcija</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let narudzbina of narudzbine">
                    <td>{{ narudzbina.gost }}</td>
                    <td>{{ formatDate(narudzbina.datum) }}</td>
                    <td>
                        <ul>
                            <li *ngFor="let deo of narudzbina.deoNarudzbine">
                                <strong>Jelo: </strong> {{deo.jeloObjekat.naziv}}. <strong>Kolicina: </strong> {{ deo.kolicina }}
                            </li>
                        </ul>
                    </td>
                    <td class="buttonDisplay">
                        <button (click)="prikaziCheckBox()" *ngIf="checkBoxFlag == false">Potvrdi</button>
                        <section *ngIf="checkBoxFlag == true">
                            Procenjeno vreme dostave: <select [(ngModel)]="minvreme">
                                <option [value]="20">20-30 minuta</option>
                                <option [value]="30">30-40 minuta</option>
                                <option [value]="50">50-60 minuta</option>
                            </select>
                            <button (click)="potvrdiNarudzbinu(narudzbina.id)">Potvrdi</button>
                            <span style="color: red;">{{errorVreme}}</span>
                        </section>
                        <button (click)="odbijNarudzbinu(narudzbina.id)" *ngIf="checkBoxFlag == false">Odbij</button>
                    </td>
                </tr>
            </tbody>
        </table>
    </section>
    <section *ngIf="flagStatistika == true">
        <div class="chart-container">
            <canvas #columnChart></canvas>
        </div>
        <div class="chart-container">
            <canvas #pieChart></canvas>
        </div>
        <div class="chart-container">
            <canvas #histogramChart></canvas>
        </div>
    </section>
</div>
